import React from 'react';
import axios from './axios_func';
import REQUESTS from './request';
import {useState, useEffect} from 'react';

function Banner() {

    const [state, setstate] = useState([]);
    useEffect(() => {
       async function fetchData_banner(){
            const requestt = await axios.get(REQUESTS.fetchMovies);
            setstate(requestt.data.results[Math.floor(Math.random() * 6) + 1 ].poster_path);
       }
       fetchData_banner();
    }, []);

    console.log(state);
    return (
        <header className="banner"  
        
        style={{backgroundSize:"cover",
                backgroundImage:`url("https://image.tmdb.org/t/p/original/riYInlsq2kf1AWoGm80JQW5dLKp.jpg")`  ,
                backgroundPosition: "center center"
        }}
        
        >

            <div className="banner_stuff">
                <h1>this.state.poster_path</h1>
            </div>
             
        </header>
    )
}

export default Banner
